// if(localStorage.Users) initialstate.users = JSON.parse(localStorage.Users)
const initialstate = {
    nicknameUser: {},
    profiles: [],
    user: {},
    filterBy: null

}

export default function UserReducer(state = initialstate, action) {
    switch (action.type) {
        case 'SET_USERS':
            return {
                ...state,
                profiles: action.users
            }
        case 'GET_USER':
            return {
                ...state,
                user: action.user
            }
        case 'SET_USER':
            return {
                ...state,
                user: action.user
            }
        case 'SET_NETFLIX_USER':
            return {
                ...state,
                nicknameUser: action.nicknameLogin.nickname
            }
        case 'FILTER_BY':
            return {
                ...state,
                filterBy: action.filterBy
            }


        default: return state
    }
}
