const initialstate = {
    netflixUser: {},
    users: [],
    user: {},
    filterBy: null

}
export default function UserReducer(state = initialstate, action) {
    switch (action.type) {
        case 'GET_USERS':
            return {
                ...state,
                users: [...action.users[0].profiles]
            }
        case 'GET_USER':
            return {
                ...state,
                user: action.user
            }
        case 'SET_USER':
            return {
                ...state,
                user: action.user
            }
        case 'SET_NETFLIX_USER':
            return {
                ...state,
                netflixUser: action.nicknameLogin.nickname
            }
        case 'FILTER_BY':
            return {
                ...state,
                filterBy: action.filterBy
            }


        default: return state
    }
}
