import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

const BASE_URL_IMG = 'https://image.tmdb.org/t/p/original'


export function MoveiList({ title, requests, toggleModal, idx }) {
    const { filterByName } = useSelector(state => state.moviesModule)
    const [movies, setMovies] = useState([])
    const [highRow, sethigtRow] = useState(false)

    useEffect(async () => {
        (idx === 0 || idx === 3) ? sethigtRow(true) : sethigtRow(false)
        const movies = await requests
        setMovies(movies)
    }, [])

    useEffect(() => {
        console.log('filterBy is:', filterByName);

    }, [state])
    function filterBy() {
        console.log('filterBy in list  is:', filterByName);
    }
    return (
        <div >
            <p>{title}</p>
            <p>{filterByName}</p>
            <div className="row-movie">
                {movies.map(movie => {
                    return <img
                        key={movie.id}
                        onClick={() => toggleModal(movie)}
                        className={`${highRow ? 'highRow' : 'lowerRow'}`}
                        src={`${BASE_URL_IMG}${highRow ? movie.poster_path : movie.backdrop_path}`} alt="" />
                })}
            </div>
        </div>
    )
}
