const initialstateMovies = {
    filterByName: null
}

function moviesReducer(state = initialstateMovies, action) {
    switch (action.type) {
        case 'FILTER_BY':
            return {
                ...state,
                filterByName: action.filterByName
            }

    }
}

export default moviesReducer = {
    moviesReducer,

}
