import React, { useEffect, useReducer, useRef } from 'react'
import { useDispatch } from 'react-redux';
import { setFilter } from '../store/actions/MovieAction'

// import moviesReducer from '../store/reducers/MoviesReducer'
// import { initialstateMovies } from '../store/reducers/MoviesReducer'
const initialstateMovies = {
    filterByName: 'k'
}
export default function moviesReducer(state = initialstateMovies, action) {
    console.log('action is:', action);
    switch (action.type) {
        case 'FILTER_BY':
            return {
                ...state,
                filterByName: action.filterByName
            }
        default: return state

    }
}
export function FilterMovie() {
    const inputRef = useRef(null)
    // const dispatch = useDispatch()

    const [state, dispatch] = useReducer(moviesReducer, initialstateMovies)

    useEffect(() => {
        inputRef.current.focus()
    }, [])
    useEffect(() => {
        // dispatch(setFilter({ ...state }))

        // onSetFilter({ ...state })
    }, [])
    function consol(ev) {

        dispatch({ type: 'FILTER_BY', filterByName: ev.target.value })
    }
    const { filterByName } = state
    return (
        <div>
            <input ref={inputRef} className="filterMovies" type="text" value={filterByName} onChange={(ev) => consol(ev)} placeholder="search" />

        </div>
    )
}
