import axios from "axios"
import { storageService } from "./storageService"


const BASE_URL = 'https://api.themoviedb.org/'
// const API_KEY = '966d25f68b2beabae673d956bfe0476d'
const KEY_WORD = 'movies'

export const moveiService = {
    query
}

async function query() {
    const res = await axios.get('https://api.themoviedb.org/4/list/1?api_key=966d25f68b2beabae673d956bfe0476d')
    console.log('res.data is:', res.data);
    const moveis = res.data
    storageService.saveToStorage(KEY_WORD, res.data)
    return moveis
}
