import React, { useEffect, useState } from 'react'
import { moveiService } from '../services/movieService'

export function Banner({ type }) {
    const [movie, setmovie] = useState('')
    const [imgWithType, setImgWithType] = useState('')
    console.log('movie banner is:', movie);

    useEffect(() => {
        configBannerImg()
        // getBannerImg()
    }, [])

    function configBannerImg() {
        if (!type) {
            const imgToBanner = getBannerImg()
            setmovie(imgToBanner)
        } else {
            while (imgWithType === type) {
                console.log('im in while');
                const imgToBanner = getBannerImg()
                console.log('imgToBanner is:', imgToBanner);
                setImgWithType(imgToBanner)
            }
        }
    }

    async function getBannerImg() {
        const resulte = await moveiService.query()
        const num = Math.floor(Math.random() * (49 - 0) + 0)
        const imgToBanner = resulte.items[num]
        return imgToBanner

    }

    return (
        // <section className="header">
        <header
            className="banner"
            style={{ backgroundImage: `url(https://image.tmdb.org/t/p/original/${movie.backdrop_path})` }}
        >
            <div className="banner-info">
                <span>{movie.title}</span>
                <p>{movie.overview}
                </p>
                <button className="play-movie">Play</button>
                <button className="details-movie">Details</button>
            </div>

        </header >

        // </section>
    )
}
