import React, { useEffect, useState } from 'react'
import { useHistory } from 'react-router-dom'

export function NavBar() {
    const { user } = useSelector(state => state.userModule)
    const [scroll, setscroll] = useState(false)
    const [usersModal, setUsersModal] = useState(false)
    const history = useHistory()

    useEffect(() => {
        window.addEventListener('scroll', showNav)
        return () => window.removeEventListener('scroll', showNav)
    }, [])

    useEffect(() => {
        console.log('amit', usersModal);
    }, [usersModal])

    const showNav = () => {
        if (window.scrollY > 30) setscroll(true)
        else setscroll(false)
    }


    return (
        <div className={`nav-bar ${(scroll) ? 'nav-black' : ''}`}>
            <div className="nav-content">
                <div className="left-nav">
                    <img
                        onClick={() => history.push('/')}
                        className="nav-logo"
                        src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg"
                        alt=""
                    />
                    <a > Home page</a>
                    <a > TV shows </a>
                    <a > Movies</a>
                    <a > News & Popular</a>
                    <a > My list</a>
                </div>
                <div className="right-nav">
                    <img
                        onClick={() => setUsersModal(!usersModal)}
                        className="user-avatar"
                        src={user.color}
                    />
                </div>
                {usersModal &&
                    <div className="user-modal">
                        <p>hey</p>
                    </div>
                }
                {usersModal && <div onClick={() => setUsersModal(false)} className="screen"></div>}
            </div>

        </div>
    )
}
