import blue from '../assets/img/blue.png'
import red from '../assets/img/red.png'
import yellow from '../assets/img/yellow.png'
import green from '../assets/img/green.png'

const users = [
    {
        nickname: 'weiss',
        profiles: [
            { _id: 101, name: 'Amit', movies: [], age: 24, color: red },
            { _id: 102, name: 'Maor', movies: [], age: 35, color: yellow },
            { _id: 103, name: 'Idan', movies: [], age: 31, color: blue },
            { _id: 104, name: 'Inbal', movies: [], age: 38, color: green }]
    },
    {
        nickname: 'san',
        profiles: [{ _id: 201, name: 'Shany', movies: [], age: 19, color: red },
        { _id: 202, name: 'Dvir', movies: [], age: 14, color: yellow },
        { _id: 203, name: 'Tomer', movies: [], age: 8.5, color: blue },
        { _id: 204, name: 'Rotem', movies: [], age: 24, color: green }]
    }]

function getUsers(nickname) {
    const profilesToUser = users.filter(user => user.nickname === nickname)
    console.log('profileToUser is:', profilesToUser);
    return Promise.resolve(profilesToUser)
}
function getUser(userId) {
    const user = users.find(user => user._id === userId)
    return user
}
function _makeId(length = 5) {
    var text = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (var i = 0; i < length; i++) {
        text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
}
export const userService = {
    getUsers,
    getUser,
    _makeId
}
