import React, { useEffect, useState } from 'react'
import { Banner } from '../cmps/Banner.jsx'
import { MoveiList } from '../cmps/MoveiList.jsx'
import { NavBar } from '../cmps/NavBar.jsx'
import requests from '../services/requestsService'
import ModalMovie from '../cmps/ModalMovie'
import { moveiService } from '../services/movieService'

export function Home() {
    // goBackToUsers(){

    // }
    const [movies, setMovies] = useState([])
    const [movieModal, setMovieModal] = useState('')
    const [openModal, setOpenModal] = useState(false)
    const [requests, setRequests] = useState([])


    useEffect(() => {
        getRequests()
    }, [])

    function getRequests() {
        const requests = moveiService.getRequests()
        setRequests(requests)
    }
    async function getMovies(url) {
        console.log('fetch is:', url);
        const movies = await moveiService.getMovies(url)
        // console.log('movies in home is:', movies);
        return movies
        // setMovies(movies)

    }

    function toggleModal(movieId) {
        console.log('movieId is:', movieId);
        setOpenModal(!openModal)
    }

    return (
        <div className="home-page">
            <NavBar />
            <Banner />
            {requests.map(req => {
                return <MoveiList
                    toggleModal={toggleModal}
                    title={req.title}
                    movies={getMovies(req.url)}
                />
            })}
            {/* <MoveiList title="Top Rated" highRow toggleModal={toggleModal} movies={requests.fetchTopRated} /> */}
            {/* <MoveiList title="Top Rated" highRow fetch={requests.fetchTopRated} />
            <MoveiList title="Documentaries" toggleModal={toggleModal} fetch={requests.fetchDocumentaries} />
            <MoveiList title="Action" fetch={requests.fetchActionMovies} />
            <MoveiList title="Horror" highRow fetch={requests.fetchHorrorMovies} />
            <MoveiList title="Comedy" fetch={requests.fetchComedyMovies} />
            <MoveiList title="Trending" fetch={requests.fetchTrending} /> */}
            {/* <MoveiList title="Originals" fetch={requests.fetchNetflixOriginals */}
            {openModal && <ModalMovie />}

        </div>
    )
}
