import { userService } from '../../services/usersService';

//creators
const _getUser = (users) => ({ type: 'GET_USERS', users })

// Dispatchers
//THANK
export function getUsers() {
    return async (dispatch) => {
        const users = await userService.getUsers()
        dispatch(_getUser(users))
    }
}
