import React, { useEffect, useReducer, useRef } from 'react'
// import moviesReducer from '../store/reducers/MoviesReducer'
// import { initialstateMovies } from '../store/reducers/MoviesReducer'
const initialstateMovies = {
    filterByName: null
}
export function moviesReducer(state = initialstateMovies, action) {
    switch (action.type) {
        case 'FILTER_BY':
            return {
                ...state,
                filterByName: action.filterByName
            }

    }
}
export function FilterMovie({ onSetFilter }) {
    const inputRef = useRef(null)
    const [state, dispatch] = useReducer(moviesReducer, initialstateMovies)

    useEffect(() => {
        inputRef.current.focus()
    }, [])
    useEffect(() => {
        onSetFilter({ ...state })
    }, [state])

    const { filterByName } = state
    return (
        <div>
            <input ref={inputRef} className="filterMovies" type="text" value={filterByName} onChange={(ev) => dispatch({ type: 'FILTER_BY', filterByName: ev.target.value })} placeholder="search" />

        </div>
    )
}
