import React, { useEffect, useState } from 'react'
import { userService } from '../services/usersService'
// import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { setProfile, loadProfiles } from '../store/actions/UserAction'

export function Profiles() {

    const { profiles, nicknameUser } = useSelector(state => state.userModule)
    const history = useHistory()

    const [edit, setEdit] = useState(false)
    const dispatch = useDispatch()


    useEffect(() => {
        dispatch(loadProfiles(nicknameUser));

    }, [nicknameUser, dispatch, profiles])


    async function chackUser(profileId, nicknameUser) {
        const infoProfile = { profileId, nicknameUser }
        dispatch(setProfile(infoProfile))
        history.push('/user')
    }

    return (
        <div className="profile-page">
            <img className="nav-logo" src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg" alt="" />
            <div className="main-profile">
                <h1>How watch in Netflix?</h1>

                <div className="main-users">
                    {profiles?.map(profile => {
                        return <div
                            className="user"
                            key={profile._id}>
                            <img src={profile.color}
                                onClick={() => chackUser(profile._id, nicknameUser)}
                                alt="img user" />

                            {edit && <button className="btn-edit">âœŽ</button>}
                            <p>{profile.name}</p>
                        </div>
                    })}
                </div>
                <button
                    style={{
                        backgroundColor: edit ? '#ffff' : '',
                        color: edit ? 'black' : ''
                    }}
                    onClick={() => setEdit(!edit)}>
                    {edit ? 'Done' : 'Edit profile'}
                </button>
            </div>
        </div >
    )
}
