/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { storageService } from '../services/storageService'
import { setFilter } from '../store/actions/MovieAction'
// import { setFilter } from '../store/actions/MovieAction'
import { FilterMovie } from './FilterMovie'
import { UsersModal } from './UsersModal'

export function NavBar({ changeProfile }) {
    const { user, profiles } = useSelector(state => state.userModule)
    const [profilesWithFilter, setProfilesWithFilter] = useState([])
    const [profileFromStorge, setProfileFromStorge] = useState({})
    const [usersFromStorge, setUsersFromStorge] = useState([])
    const dispatch = useDispatch()

    const [scroll, setscroll] = useState(false)
    const [usersModal, setUsersModal] = useState(false)
    const history = useHistory()

    useEffect(() => {
        window.addEventListener('scroll', showNav)
        return () => window.removeEventListener('scroll', showNav)
    }, [])

    useEffect(() => {
        const profileFromStorge = storageService.loadFromStorage('profile')
        const usersFromStorge = storageService.loadFromStorage('Profiles')
        setProfileFromStorge(profileFromStorge)
        setUsersFromStorge(usersFromStorge)

    }, [user])

    const filterUsers = (userId) => {
        if (!userId) {
            console.log('inside', profileFromStorge, usersFromStorge);
            const newUsers = usersFromStorge.filter(user => user._id !== profileFromStorge._id)
            console.log('newUsers is:', newUsers);
            return newUsers
        } else {

            const newUsers = profiles.filter(user => user._id !== userId)
            console.log('newUsers is:', newUsers);
            return newUsers
        }
    }

    const showNav = () => {
        if (window.scrollY > 30) setscroll(true)
        else setscroll(false)
    }

    const openModal = () => {
        const usersWithFilter = filterUsers(user._id)
        setProfilesWithFilter(usersWithFilter)
        setUsersModal(!usersModal)
    }



    const onSetFilter = filterBy => dispatch(setFilter(filterBy))
    // const onSetFilter = (filterBy) => {
    //     dispatch(setFilter(filterBy))
    // setFilterOnMovies(filterBy)
    // }
    //
    // function toggleHoverMouse() {
    //     console.log('amit is:');
    // }
    return (
        <div className={`nav-bar ${(scroll) ? 'nav-black' : ''}`}>
            <div className="nav-content">
                <div className="left-nav">
                    <img
                        onClick={() => history.push('/profile')}
                        className="nav-logo"
                        src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg"
                        alt=""
                    />
                    <a > Home page</a>
                    <a > TV shows </a>
                    <a > Movies</a>
                    <a > News & Popular</a>
                    <a > My list</a>
                </div>
                <div className="flex align-center right-nav">
                    <FilterMovie onSetFilter={onSetFilter} />
                    <img
                        onClick={() => openModal()}
                        // onMouseEnter={() => setUsersModal(!usersModal)}
                        // onMouseOut={() => toggleHoverMouse(false)}
                        className="user-avatar"
                        src={user?.color || profileFromStorge.color}
                    />
                    <span onClick={() => setUsersModal(!usersModal)}>â–¼</span>

                </div>
                {usersModal && <UsersModal profiles={profilesWithFilter} changeProfile={changeProfile} />}
                {usersModal && <div onClick={() => setUsersModal(false)} className="screen"></div>}
            </div>

        </div>
    )
}
