import { LoginService } from '../../services/LoginService';
import { storageService } from '../../services/storageService';
import { userService } from '../../services/usersService';


//creators
const _setUsers = (users) => ({ type: 'GET_USERS', users })
const _getUser = (user) => ({ type: 'GET_USER', user })
const _setUser = (user) => ({ type: 'SET_USER', user })
const _setNetflixUser = (netflixUser) => ({ type: 'SET_NETFLIX_USER', netflixUser })
// Dispatchers
//THANK
export function loadUsers(netflixId) {
    return async (dispatch) => {
        try {
            console.log('NETFLIXiD is:', netflixId);
            const users = await userService.getUsers(netflixId)
            console.log('users is:', users);
            dispatch(_setUsers(users))
        }
        catch (err) {
            console.log('catch to get users!', err);
        }
    }
}

export function getUser(userId) {
    return (dispatch) => {
        const user = userService.getUser(userId)
        dispatch(_getUser(user))
    }
}

export function setUser(userId) {
    return (dispatch) => {
        const user = userService.getUser(userId)
        storageService.saveToStorage('User', user)
        dispatch(_setUser(user))
    }
}

export function setNetflixUser(user) {
    return async dispatch => {
        const netflixUserLogin = await LoginService.doLogin(user)
        console.log('netflixUser is:', netflixUserLogin);
        dispatch(_setNetflixUser(netflixUserLogin))
    }
}
