import React, { useEffect, useState } from 'react'
import { userService } from '../services/usersService'
import blue from '../assets/img/blue.png'
import red from '../assets/img/red.png'
import yellow from '../assets/img/yellow.png'
import green from '../assets/img/green.png'

export function Profiles() {
    const [users, setUsers] = useState([])
    const [edit, setEdit] = useState(false)

    useEffect(async () => {
        const users = await userService.getUsers()
        setUsers(users)
    }, [])
    useEffect(() => {
        console.log('edit is:', edit);

    }, [edit])
    return (
        <div className="profile-page">
            <img className="nav-logo" src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg" alt="" />
            <div className="main-profile">
                <h1>How watch in Netflix?</h1>

                <div className="main-users">
                    {users.map((user, idx) => {
                        return <div
                            className="user"
                            key={idx}>
                            <img src={user.color} alt="" />
                            {/* <img src={require(`${blue}`)} alt="" /> */}
                            {edit && <button className="btn-edit">âœŽ</button>}
                            <p>{user.name}</p>
                        </div>
                    })}
                </div>
                <button
                    style={{
                        backgroundColor: edit ? '#ffff' : '',
                        color: edit ? 'black' : ''
                    }}
                    onClick={() => setEdit(!edit)}>
                    {edit ? 'Done' : 'Edit profile'}
                </button>
            </div>
        </div >
    )
}
