import React, { useEffect, useState } from 'react'
import { moveiService } from '../services/movieService'
import ModalMovie from './ModalMovie'

const BASE_URL_IMG = 'https://image.tmdb.org/t/p/original'


export function MoveiList({ title, fetch, highRow }) {
    const [movies, setMovies] = useState([])
    const [openModal, setOpenModal] = useState(false)
    const [movieModal, setMovieModal] = useState('')

    useEffect(() => {
        getMovies()
    }, [])

    async function getMovies() {
        const movies = await moveiService.getMovies(fetch)
        setMovies(movies)

    }
    function toggleModal(movieId) {
        // const movie
        setOpenModal(!openModal)
    }

    return (
        <div className="row-container">
            <p>{title}</p>
            <div className="row-movie">
                {movies.map(movie => {
                    return <img
                        key={movie.id}
                        onClick={() => toggleModal(movie.id)}
                        className={`${highRow ? 'highRow' : 'lowerRow'}`}
                        src={`${BASE_URL_IMG}${highRow ? movie.poster_path : movie.backdrop_path}`} alt="" />
                })}
            </div>
            {openModal && <ModalMovie />}
        </div>
    )
}
