import axios from "axios"
import { storageService } from "./storageService"


const BASE_URL = 'https://api.themoviedb.org/'
const KEY_WORD = 'movies'


const API_KEY = '966d25f68b2beabae673d956bfe0476d'


const requests = [

    { fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US` },
    { fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US` },
    { fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28` },
    { fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99` },
    { fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35` },
    { fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27` },
    { fetchNetflixOriginals: `/dicover/tv?api_key=${API_KEY}&with_networks=213` },
]


async function query() {
    // const res = await axios.get('https://api.themoviedb.org/3/list/1?api_key=966d25f68b2beabae673d956bfe0476d')
    // const moveis = res.data
    // storageService.saveToStorage(KEY_WORD, res.data)
    const movies = storageService.loadFromStorage(KEY_WORD)
    console.log('movies is:', movies);

    return movies
}
function getRequests() {
    console.log('requests is:', requests);
}

async function getMovies(fetch) {
    const req = await axios.get(`https://api.themoviedb.org/3${fetch}`)
    const movies = req.data.results
    return movies
}

export const moveiService = {
    query,
    getMovies,
    getRequests
}
