import React, { useEffect, useState } from 'react'
// import { userService } from '../services/usersService'
// import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { getUser, getUsers } from '../store/actions/UserAction'

export function Profiles() {

    // const [users, setUsers] = useState([])
    const { users } = useSelector(state => state.userModule)
    const history = useHistory()

    const [edit, setEdit] = useState(false)
    const dispatch = useDispatch()

    useEffect(async () => {
        dispatch(getUsers())
    }, [])

    useEffect(() => {

    }, [edit])

    function chackUser(userId) {
        dispatch(getUser(userId))
        history.push('/user')
    }

    return (
        <div className="profile-page">
            <img className="nav-logo" src="https://upload.wikimedia.org/wikipedia/commons/0/08/Netflix_2015_logo.svg" alt="" />
            <div className="main-profile">
                <h1>How watch in Netflix?</h1>

                <div className="main-users">
                    {users.map(user => {
                        return <div
                            className="user"
                            key={user._id}>
                            <img
                                src={user.color}
                                onClick={() => chackUser(user._id)}
                            />
                            {edit && <button className="btn-edit">âœŽ</button>}
                            <p>{user.name}</p>
                        </div>
                    })}
                </div>
                <button
                    style={{
                        backgroundColor: edit ? '#ffff' : '',
                        color: edit ? 'black' : ''
                    }}
                    onClick={() => setEdit(!edit)}>
                    {edit ? 'Done' : 'Edit profile'}
                </button>
            </div>
        </div >
    )
}
