import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { moveiService } from '../services/movieService'

const BASE_URL_IMG = 'https://image.tmdb.org/t/p/original'


export function MoveiList({ title, fetch, lowerRow }) {
    const [movies, setMovies] = useState([])
    useEffect(() => {
        const movies = getMovies()
    }, [])
    async function getMovies() {
        const req = await axios.get(`https://api.themoviedb.org/3${fetch}`)
        const movies = req.data.results
        console.log('movies is:', movies);
        setMovies(movies)
        return movies
    }
    return (
        <div className="row-movie">
            <p>{title}</p>
            {movies.map(movie => {
                return <img
                    key={movie.id}
                    src={`${BASE_URL_IMG}${lowerRow ? movie.poster_path : movie.backdrop_path}`} alt="" />
            })}
        </div>
    )
}
