const initialstate = {
    users: [],
    user: {},
    filterByName: null

}
export default function UserReducer(state = initialstate, action) {
    console.log('action is:', action);
    switch (action.type) {
        case 'GET_USERS':
            return {
                ...state,
                users: action.users
            }
        case 'GET_USER':
            return {
                ...state,
                user: action.user
            }
        case 'FILTER_BY':
            return {
                ...state,
                filterByName: action.filterByName
            }


        default: return state
    }
}
